void LEVELPL::sceneLogic(uint32_t deltaT)
{
  static bool pressJump = false;
  static bool jump = false;
  static vector2 jumpCheck;
  static vector2 oldPos;
  static vector2 currentPos;
  vector2 position(0,8);

  if(pressJump && currentPos.y > jumpCheck.y && jump)
  {
    position.y = -10;
  }
  else
  {
    jump = false;
  }
  


    objectSettings one = player->getValues();

    if(gamePl->getJoyPad(LEFT))
    {
      if((one.position.x-4) > 0)
      {
        position.x =-4;

      }
      player->startAnimation(0, false);
      player->setShowTexture(1);
    }
    else if(gamePl->getJoyPad(RIGHT))
    {
      if((one.position.x +4) < 480)
      {
        position.x =4;
      }
      player->startAnimation(1, false);
      player->setShowTexture(0);
    }
    else
    {
        player->stopAnimation();
    }
    


    if(gamePl->getButton(B_BUTTON) && !pressJump)
    {
      pressJump = true;
      jumpCheck = currentPos;
      jumpCheck.y -= 70;
      jump = true;
      audioPl->startPlaying(CHANNEL3,0,true);
    }

    if(pressJump && (currentPos.y == oldPos.y) && !gamePl->getButton(B_BUTTON))
      pressJump = false;
    
    
    if(gamePl->getButton(SELECT) || one.position.y > 200)
    {
      vector2 playerPos(30,55);
      player->setPosition(playerPos);
    }

    if(gamePl->getButton(START))
    {
      gamePl->loadScene(SELECT_SCENE);
    }
    position.x *= (deltaT/8);
    oldPos = currentPos;
    currentPos = platform->moveCollisionWallGround(player,position);

  

  
}

void setupPlatform()
{ 
    platform = new LEVELPL();
    player = new KinectBody();
    cam = new CAMERA2D();

    tileMap settingMap;
    vector2 playerPos(30,55);
    vector2 playerSize(32,32);
    vector2 tileSize(32,32);
    vector2 playerCol(20,32);
    vector2 playNamePosition(10,0);
    vector2 zero;
    
    idle[0] = rightIdle;
    idle[1] = leftIdel;

    player->setTexture(playerSize,idle,playerPos,0,0xff);
    player->setupCollisionWindow(zero,playerCol,(collisionType )GROUND_AND_WALL);


    
    camera_s camSettings;
    vector2 camPosition;
    camSettings.cameraOn = true;
    camSettings.objectToCamera = player;
    camSettings.notFollowAreaRect.x = 50;
    camSettings.notFollowAreaRect.y = 50;
    camSettings.position = camPosition;
    camSettings.mapeSize.x = 480;
    camSettings.mapeSize.y = 512;   
    camSettings.tileSize = playerSize;
  

    cam->setCamera(camSettings);

    walkLeft[0] = leftWalk1;
    walkLeft[1] = leftIdel;
    walkLeft[2] = leftWalk3;

    walkRight[0] = rightWalk1;
    walkRight[1] = rightIdle;
    walkRight[2] = rightWalk3;

    player->setAnimation(walkLeft,3,1,0xff);
    player->setAnimation(walkRight,3,1,0xff);

    settingMap.tileSize = tileSize;
    settingMap.amountTile.x = 15;
    settingMap.amountTile.y = 16;
    settingMap.order = objectOrder;

    platform->addTexture(grass,-1,true);
    platform->addTexture(dirt,-1,false);
    platform->setTileMap(settingMap,OBJECT_LAYER);

    settingMap.order = backgroundOrder;
    platform->setTileMap(settingMap,BACKGROUND_LAYER);

    platform->addKinect(player);
    platform->addCamera(cam);
    platform->addUI(&tablePl);

    gamePl = MICROGAMEATZERO::getInstance();
    audioPl = AUDIOENGINE::getInstance();

    audioPl->setChannel(CHANNEL3,jump,80,sizeof(jump));
    gamePl->addNewScene(platform);
}

